mapper.insert:
INSERT INTO board 
(
	user_no,
	board_title,
	board_content
) VALUES (
	#{userNo},
	#{boardTitle},
	#{boardContent}
)

INSERT INTO board_category
(
	board_no,
	category_no
) values (
	CURRVAL('seq_board'::regclass),
	#{categoryNo}
)

INSERT INTO tag
(
	board_no,
	concert_no
) values (
	CURRVAL('seq_board'::regclass),
	#{concertNo}
)

mapper.select:
SELECT b.board_no, u.user_no, u.nickname, b.board_level, b.board_write_date, b.board_title, b.board_content, b.board_count FROM board b JOIN staez_user u on (b.user_no = u.user_no) JOIN board_category bc on (b.board_no = bc.board_no) WHERE b.board_status = 'Y' and bc.category_no IN
#{item}
ORDER BY b.board_no DESC

SELECT file_path || change_name
FROM profile_img
WHERE user_no = #{userNo}

SELECT COUNT(*)
FROM board_like
WHERE board_no = #{boardNo}
AND user_no = #{userNo}
AND status = 'Y'

SELECT COUNT(*)
FROM reply
WHERE board_no = #{boardNo}

mapper.update:
UPDATE board
SET 
	user_no = #{userNo},
	board_title = #{boardTitle},
	board_content = #{boardContent}
WHERE board_no = #{boardNo}

INSERT INTO board_category
(
	board_no,
	category_no
) values (
	#{boardNo},
	#{categoryNo}
)

UPDATE tag
SET concert_no = #{concertNo}
WHERE board_no = #{boardNo}

UPDATE board
SET board_status = 'N'
WHERE board_no = #{boardNo}

mapper.select:
SELECT
	COUNT(*)
FROM board_like
WHERE board_no = #{boardNo}
AND user_no = #{userNo}

mapper.update:
UPDATE board_like
SET status = #{status}
WHERE board_no = #{boardNo}
AND user_no = #{userNo}

mapper.insert:
INSERT INTO board_like
(
	board_no,
	user_no
) VALUES (
	#{boardNo},
	#{userNo}
)
