mapper.select:

 	    SELECT category_no, category_name
		FROM category
		WHERE ref_category_no = 1;

			SELECT COUNT(*)
			FROM concert_like
           WHERE user_no = #{userNo}
             AND concert_no = #{concertNo}
             AND status = 'Y'

			SELECT COUNT(*)
			FROM concert_like
           WHERE user_no = #{userNo}
             AND concert_no = #{concertNo}

mapper.insert:
INSERT INTO concert_like (
    user_no,
    concert_no
) VALUES (
    #{userNo},
    #{concertNo}
)

mapper.update:
UPDATE concert_like
   SET status = #{status}
 WHERE user_no = #{userNo}
   AND concert_no = #{concertNo}

mapper.select:
SELECT c.concert_no, c.concert_title, ca.change_name, cs.start_date, cs.end_date, ca.file_path, ct.category_name, t.address FROM concert c JOIN theater t USING (theater_no) JOIN concert_schedule cs USING (concert_no) JOIN concert_attachment ca ON c.concert_no = ca.concert_no JOIN concert_category cc on c.concert_no = cc.concert_no JOIN category ct ON cc.category_no = ct.category_no WHERE c.concert_status = 'Y' AND cc.category_no = #{categoryNo}
AND address
LIKE #{area} || '%'
